import React, { useState, useEffect } from "react";
import { Avatar, Divider, Modal, Menu, Button } from "@mui/material";
import axios from 'axios';
import { useLocation } from "react-router-dom";
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import UserDetails from './userDetails';
import { useNavigate } from 'react-router-dom';
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import AccordionDetails from '@mui/material/AccordionDetails';
import './component.css'

const ProfilePage = () => {
    const [tab, settab] = useState(1)
    const location = useLocation();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [userList, setUserList] = useState([])
    const [userData, setUserData] = useState(location?.state?.user)
    const handleClick = (event) => {
        setAnchorEl(anchorEl ? null : event.currentTarget);
    };
    const navigate = useNavigate();
    const open = Boolean(anchorEl);
    const id = open ? 'simple-popper' : undefined;
    console.log(location.state.user, 'history');
    // let userData = location?.state?.user;

    const tabChange = (value) => {
        settab(value)
    }
    const handleClose = () => {
        setAnchorEl(null);
    };

    useEffect(() => {
        fetchUserList()
    }, [])

    const fetchUserList = () => {
        axios
            .get('https://panorbit.in/api/users.json')
            .then((res) => {
                console.log(res, 'result');
                setUserList(res?.data?.users)
            })
            .catch((error) => {
                console.log(error);
            });
    };

    return (
        <div className="col-md-12">
            <div className="col-md-12" style={{ display: 'flex', height: '100vh' }} >
                <div className="col-md-3 d-flex justify-content-center">
                    <div className="menu-item">
                        <div className="tabs d-flex align-items-center" style={{ flexDirection: 'column' }}>
                            <div className="col-md-12 py-4 ps-4 d-flex">
                                <div className="col-md-10" onClick={() => tabChange(1)} style={{ cursor: 'pointer' }} >
                                    <div style={{ fontSize: '20px', textAlign: 'left', color: '#A6A5E4' }} className="">Profile</div>
                                    <Divider style={{ color: '#A6A5E4', height: '2px' }} />
                                </div>
